import { Component, OnInit } from '@angular/core';
import { CerveceriaService } from 'src/app/services/cerveceria/cerveceria.service';
import { RoutingService } from 'src/app/services/routing/routing.service';

@Component({
  selector: 'app-manipulate-cerveceria',
  templateUrl: './manipulate-cerveceria.component.html',
  styleUrls: ['./manipulate-cerveceria.component.scss']
})
export class ManipulateCerveceriaComponent implements OnInit {

  constructor(private routingSrv: RoutingService, private cerveceriaSrv: CerveceriaService) { }

  ngOnInit(): void {
  }

  async verifyData(event: any){
    if(!event){
      this.routingSrv.goBack();
    } else{
      await this.cerveceriaSrv.saveCerveceria(null);
    }
  }

}
<app-layout>
    <h1 class="page-title">Agregando cervecería</h1>
    <div class="page-content">
        <app-manipulate-item (infoBack)="verifyData($event)" [categoria]="'cervecerias'"></app-manipulate-item>
    </div>
</app-layout>
